import React, { useMemo } from 'react';
import { getCookie } from '../utils/Cookies';
const Menu = ({ props = [] }) => {
  const renderedMenuItems = useMemo(() => {
    const addedMenus = new Set();

    return props
      .filter(item => {
        if (!addedMenus.has(item?.id)) {
          addedMenus.add(item?.id);
          return true;
        }
        return false;
      })
      .map((item,index) => {
        const menu = item?.menu.toLowerCase();

        return (
          <li key={index}>
            <a href={`/${menu}`}>
              <div className="menu-icon">
                {icons[menu]}
              </div>
              <span className="nav-text">{item?.menu}</span>
            </a>
          </li>
        );
      });
  }, [props]);

  return (
    <div className="deznav">
      <div className="deznav-scroll mm-active">
        <ul className="metismenu mm-show" id="menu">
          {renderedMenuItems.length ? renderedMenuItems : <li>Nenhum item</li>}
        </ul>
      </div>
    </div>
  );
};

export default Menu;


const Preloader = () => {
  return (
    <div id="preloader">
      <div className="lds-ripple">
        <div />
        <div />
      </div>
    </div>
  );
};

const Footer = () => {
  return (
    <div className="footer">
      <div className="copyright">
        <p>
          Copyright Â© Developed <span>2024</span>
        </p>
      </div>
    </div>
  );
};

const Container = ({ children }) => {
  return (
    <div className="container-fluid">
      <div className="row">{children}</div>
    </div>
  );
};

const icons = {
  home: <i className={`fas fa-home ${getCookie('theme') === 'light' || getCookie('theme') === null ? 'text-dark' : 'text-light'}`} />
  ,

  usuario: <i className={`fas fa-user ${getCookie('theme') === 'light' || getCookie('theme') === null ? 'text-dark' : 'text-light'}`} />,
};
export { Menu, Preloader, Footer, Container };
